import React, { useState, useEffect } from 'react';
import Routers from './routers';
import apiRequest from './api/productApi';

function App() {

  const [products, setProducts] = useState([]);
  // Danh sách sản phẩm
  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data } = await apiRequest.getAll();
        setProducts(data);
      } catch (error) {
        console.log('failed to request API: ', error)
      }
    }
    getProducts();
  }, []);

  // Xóa sản phẩm
  const onHandleRemove = async (id) => {
    try {
      const { data } = await apiRequest.remove(id);
      const newProducts = products.filter(product => product.id !== data.id);
      setProducts(newProducts);
    } catch (error) {
      console.log('failed to request API: ', error)
    }

  }

  const onHandleAdd = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product
    }
    setProducts([
      ...products,
      newProduct
    ])
  }
  const onHandleEdit = (id,product)=>{
    const abc = {...product};
    const newProducts =products.map(pro =>(pro.id==id ? {...product} : pro));
    setProducts(newProducts);
  }
  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove}  onAdd ={onHandleAdd} onEdit ={onHandleEdit}/>
    </div>
  )

}
export default App;
