import React , {useState} from 'react'
import PropTypes from 'prop-types'
import {useParams} from 'react-router-dom'
import { useForm } from 'react-hook-form'
import { useHistory } from "react-router-dom"
import {storage} from "../../../../components/firebase/index"


 

const EditProducts = ({ products, onUpdate }) => {
  let { id } = useParams();
  let history = useHistory();
  const [image, setImage] = useState(null);
  const [url,setUrl] = useState("")
  const product = products.find(product => product.id === id);
  const { register, handleSubmit, errors } = useForm(); // Sử dụng hook form
  const [currentProduct, setCurrentProduct] = useState(product);

  const onHandleSubmit = (e) => {
      // e.preventDefault();
      const uploadTask = storage.ref(`image/${image.name}`).put(image);
      uploadTask.on(
        "state_changed",
        snapshot=>{},
        error=>{
          console.log(error);
        },
        ()=>{
          storage
          .ref("image")
          .child(image.name)
          .getDownloadURL()
          .then(url=>{
            setUrl(url);
            console.log(url);
          });
        }
      )
      if(url){
        currentProduct.image = url;
        onUpdate(currentProduct);
        history.push('/admin/products');
      }
  }
  const onHandleChange = (e) => {
    if(e.target.files){
        setImage(e.target.files);
      }
      const { name, value } = e.target;
      setCurrentProduct({
          ...currentProduct,
          [name]: value
      })
  }
    return (
        <div className="content-wrapper">
        <div className="card-header py-3">
             <h1 className="h3 mb-2 text-gray-800">Edit Products</h1>
            <form action='' onSubmit ={handleSubmit(onHandleSubmit)}>
            <div className="form-group">
            <label htmlFor="exampleInputEmail1">Tên Sản Phẩm</label>
            <input   
                    // value={currentProduct.name}
                    onChange = {onHandleChange}
                    type="text" className="form-control" 
                    placeholder="Tên Sản Phẩm" 
                    name ="name"
                    ref={register({
                      required: "Hãy nhập tên sản phẩm",
                      minLength: 5,
                    })}
            />
             {errors.name && errors.name.message}
             {errors.name && errors.name.type === "minLength" && <p>Nhập tối thiểu 5 ký tự</p>}
            </div>
            <div className="form-group">
            <label htmlFor="exampleInputEmail1">Ảnh Sản Phẩm</label>
            {/* <img src={currentProduct.image} width="150px" ></img> */}
            <input   
                    // defaultValue={currentProduct.image}
                    onChange = {onHandleChange}
                    type="text" 
                    className="form-control"  
                    placeholder="Ảnh Sản Phẩm" 
                    name="image"
                    ref={register({
                        required: "Hãy nhập ảnh",
                        minLength: 5,
                      })}
            />
            {errors.image && errors.image.message}
            {errors.image && errors.image.type === "minLength" && <p>Nhập tối thiểu 5 ký tự</p>}
            </div>
            <div className="form-group">
            <label htmlFor="exampleInputEmail1">Giá sản phẩm</label>
            <input  
                    value={currentProduct.price}
                    onChange = {onHandleChange}
                    type="text" 
                    className="form-control"  
                    placeholder="Giá Sản Phẩm" 
                    name="price"
                    ref={register({
                        required: "Hãy nhập Giá sản phẩm",
                        minLength: 5,
                        pattern:{
                            value: /^\d{0,10}$/,
                        }
                      })}
            />
            {errors.price && errors.price.message}
            {errors.price && errors.price.type === "minLength" && <p>Nhập tối thiểu 5 ký tự</p>}
            {errors.price && errors.price.type === "pattern" && <p>Nhập Số</p>}
            </div>
        <button type="submit" className="btn btn-success">UpDate</button>
      </form>
    </div>
    </div>
    )
}

EditProducts.propTypes = {

}

export default EditProducts
