import React , {useState} from 'react'
import PropTypes from 'prop-types'
import {useParams} from 'react-router-dom'
import { useForm } from 'react-hook-form'
 

const EditProducts = ({ onAdd , products, onEdit }) => {
  console.log(products)
  const { handleSubmit, register, errors } = useForm();
  const { id } = useParams();
  const product = id?products.find(product => product.id == id) : {};
  const [valueInput, setValueInput] = useState(product);

  const onHandleChange = (e) => {
      const { name, value } = e.target;
      
      setValueInput({
          ...valueInput,
          [name]: value
      })
  }
  
  const onHandleSubmit = data => {
      if(data){
          if(!id) {
              onAdd(valueInput);
              data.preventDefault();
              
          }else{
              onEdit(id, valueInput);
              data.preventDefault();
          }
         
      }
  }
    return (
        <div>
            <div className="content-wrapper">
        <div className="card-header py-3">
             <h1 className="h3 mb-2 text-gray-800">Add Product</h1>
            <form action='' onSubmit ={handleSubmit(onHandleSubmit)}>
            <div className="form-group">
            <label htmlFor="exampleInputEmail1">Tên Sản Phẩm</label>
            <input   
                    defaultValue ={valueInput.name || ""}
                    onChange = {onHandleChange}
                    type="text" className="form-control" 
                    placeholder="Tên Sản Phẩm" 
                    name ="name"
                    ref={register({
                      required: "Hãy nhập tên sản phẩm",
                      maxLength: 15,
                      minLength: 5,
                    })}
            />
             {errors.name && errors.name.message}
             {errors.name && errors.name.type === "maxLength" && <p>Nhập tối đa 15 ký tự</p>}
             {errors.name && errors.name.type === "minLength" && <p>Nhập tối thiểu 5 ký tự</p>}
            </div>
            <div className="form-group">
            <label htmlFor="exampleInputEmail1">Ảnh Sản Phẩm</label>
            <input   
                    defaultValue ={valueInput.image || ""}
                    onChange = {onHandleChange}
                    type="text" 
                    className="form-control"  
                    placeholder="Ảnh Sản Phẩm" 
                    name="image"
                    ref={register({
                        required: "Hãy nhập ảnh",
                        maxLength: 15,
                        minLength: 5,
                      })}
            />
            {errors.image && errors.image.message}
            {errors.image && errors.image.type === "maxLength" && <p>Nhập tối đa 15 ký tự</p>}
            {errors.image && errors.image.type === "minLength" && <p>Nhập tối thiểu 5 ký tự</p>}
            </div>
            <div className="form-group">
            <label htmlFor="exampleInputEmail1">Ảnh Sản Phẩm</label>
            <input  
                    defaultValue ={valueInput.price || ""}
                    onChange = {onHandleChange}
                    type="text" 
                    className="form-control"  
                    placeholder="Giá Sản Phẩm" 
                    name="price"
                    ref={register({
                        required: "Hãy nhập Giá sản phẩm",
                        maxLength: 15,
                        minLength: 5,
                        pattern:{
                            value: /^\d{0,10}$/,
                        }
                      })}
            />
            {errors.price && errors.price.message}
            {errors.price && errors.price.type === "maxLength" && <p>Nhập tối đa 15 ký tự</p>}
            {errors.price && errors.price.type === "minLength" && <p>Nhập tối thiểu 5 ký tự</p>}
            {errors.price && errors.price.type === "pattern" && <p>Nhập Số</p>}
            </div>
        <button type="submit" className="btn btn-success">Thêm sản phẩm</button>
      </form>
    </div>
    </div>
        </div>
    )
}

EditProducts.propTypes = {

}

export default EditProducts
