import React, { useState, useEffect } from 'react';
import Routers from './routers';
import apiRequest from './api/productApi'
import apiRequestCate from'./api/categoriesApi';


function App() {
  const [categories, setCategories] = useState([]);
  const [products, setProducts] = useState([]);
  // Phần sản phẩm
  // Danh sách sản phẩm
  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data } = await apiRequest.getAll();
        setProducts(data);
      } catch (error) {
        console.log('failed to request API: ', error)
      }
    }
    getProducts();
  }, []);
  // Xóa sản phẩm
  const onHandleRemove = async (id) => {
    try {
      const { data } = await apiRequest.remove(id);
      const newProducts = products.filter(product => product.id !== id);
      const newCategories = categories.filter(categori => categori.id !== id);
      setProducts(newProducts);
      setCategories(newCategories);
      alert("Xóa thành công")
    } catch (error) {
      console.log('failed to request API: ', error)
    }
  }
  // Thêm sản phẩm
  const onHandleAdd = async (product) => {
    try {
      const { data } = await apiRequest.create(product);
      setProducts([
        ...products,
        data
      ])
      alert("Đã thêm thành công !");
    } catch (error) {
      console.log('failed to request API: ', error)
    }
  }

  // Cập nhật product 
  const onHandleUpdate = async (updateProduct) => {
    try{
      const{data} = await apiRequest.update(updateProduct.id, updateProduct);
      const newProducts = products.map(product => (
        product.id === updateProduct.id ? updateProduct : product  // Nếu product.id bằng với id của sản phẩm vừa chỉnh sửa thì trả về mảng có object mới
      ));
      setProducts(newProducts);
      alert("Đã sửa thành công !");
    }
    catch (error){
      console.log('failed to request API: ', error)
    }
  // Phần danh mục
  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data } = await apiRequest.getAll();
        setProducts(data);
      } catch (error) {
        console.log('failed to request API: ', error)
      }
    }
    getProducts();
  }, []);




  //   const newProducts = products.map(product => (
  //     product.id === updateProduct.id ? updateProduct : product  // Nếu product.id bằng với id của sản phẩm vừa chỉnh sửa thì trả về mảng có object mới
  //   ));
  //   localStorage.setItem('products', JSON.stringify(newProducts))
  //   setProducts(newProducts);
  // }
    }
  return (
    <div className="App">
      <Routers
       categories ={categories} products={products} onRemove={onHandleRemove}  onAdd ={onHandleAdd} onUpdate ={onHandleUpdate} />
    </div>
  )

}
export default App;
